"use strict";

// if.... else
// if = se
// else = senão
var temperature = 35;

if (temperature >= 38) {
  console.log("Febre Alta");
} else if (temperature <= 37.9) {
  console.log("Febre Moderada");
} else {
  console.log("Saudável");
} // if(condicional) { // sempre que a condicional retornar true, onde o programa executará}


{// se a condicional for false então o else entrará em ação.
}