"use strict";

/*
 * Expressões e Operadores 
 * 
 * - Expressions
 * - Operadores
 *      Binary
 *      Unary
 *      Ternary 
 */
var number = 1; // Expressão é qualquer linha de código que resolva um problema

console.log(number);
var number2 = 18;
console.log(number2 + 1); // operador binário

console.log(++number2); // operador unário

console.log(--number2); // operador unário

