"use strict";

// função é um tipo de dado estrutural
// criar aplicativos de frases motivacionais
console.log("Estudar é muito bom!");
console.log("Paciência e persistência");
console.log("Revisão é a mão do aprendizado");
/* 
* Estrutura da função 
* function creat() {
}
*/
// Passo Nº 1: declaração da função - function statement/declaration

function creatPhrases() {
  console.log("Estudar é muito bom!");
  console.log("Paciência e persistência");
  console.log("Revisão é a mão do aprendizado");
} // Passo Nº 2: Executar a função (rodar, invocar, chamar) - (execute, call, invoke)


creatPhrases();
console.log('fim do programa');
/*
Pra quê serve a função:
1º -  Agrupamento de código
2º - Reutilização de código
3º - Dar um significado ao bloco de código
Ex: criar aplicativos de frases motivacionais
function creatPhrases() {
    console.log("Estudar é muito bom!")
    console.log("Paciência e persistência")
    console.log("Revisão é a mão do aprendizado")
} 
*/